import * as React from "react";
import { NavigationContainer, DefaultTheme } from "@react-navigation/native";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { ProfileScreen, ProjectScreen, ChartScreen, HomeScreen } from "./Views";
import axios from "axios";
import { Icon } from "react-native-elements";

const darkTheme = {
  dark: true,
  colors: {
    primary: "rgb(255, 45, 85)",
    background: "#000",
    card: "#292D39",
    text: "white",
    border: "#292D39",
    notification: "rgb(255, 69, 58)",
  },
};
const uid = "8b3f2aa1e1b4e9ea2ed2399b3783dba79813b948c9ecbb9555f7927a7b530e0a";
const client =
  "fdf4044597bc43b22cf442e825051dda952c834d1d16d8aebfdb6c223df3a37d";
const Drawer = createDrawerNavigator();

export default function App() {
  const Home = ({ navigation }) => (
    <HomeScreen
      navigation={navigation}
      set={setRes}
      token={token}
      theme={dark}
      setTheme={setDark}
    />
  );

  const Profile = ({ route, navigation }) => (
    <ProfileScreen rslt={result} route={route} dark={dark} />
  );
  const Project = ({ route, navigation }) => (
    <ProjectScreen
      navigation={navigation}
      route={route}
      rslt={result}
      dark={dark}
    />
  );
  const [result, setRes] = React.useState([]);
  const [token, setToken] = React.useState("");
  const [delay, setDelay] = React.useState(7200000);
  const [dark, setDark] = React.useState(false);
  React.useEffect(() => {
    console.log("DKHALT");
    axios
      .post(
        "https://api.intra.42.fr/oauth/token",
        {
          grant_type: "client_credentials",
          client_id: uid,
          client_secret: client,
        },
        { timeout: 2000 }
      )
      .then((tk) => {
        setToken(tk.data?.access_token);
        setDelay(tk.data?.expires_in);
      })
      .catch((er) => console.log(er));
    console.log(token);
  }, []);
  // setInterval(() => {
  //   alert("Expire");
  //   axios
  //     .post("https://api.intra.42.fr/oauth/token", {
  //       grant_type: "client_credentials",
  //       client_id: uid,
  //       client_secret: client,
  //     })
  //     .then((tk) => setToken(tk?.data.access_token));
  // }, delay);

  return (
    <NavigationContainer
      theme={dark ? darkTheme : DefaultTheme}
      initialRouteName="Profile"
    >
      <Drawer.Navigator>
        <Drawer.Screen
          name="Home"
          component={Home}
          options={{
            headerShown: false,
          }}
        />
        <Drawer.Screen
          name="Profile"
          component={Profile}
          options={({ navigation }) => ({
            headerRight: () => (
              <Icon
                type="font-awesome-5"
                name="sign-out-alt"
                color="#000"
                onPress={() => navigation.navigate("Home")}
              />
            ),
          })}
        />
        <Drawer.Screen
          name="Projects"
          component={Project}
          options={({ navigation }) => ({
            headerRight: () => (
              <Icon
                type="font-awesome-5"
                name="sign-out-alt"
                color="#000"
                onPress={() => navigation.navigate("Home")}
              />
            ),
          })}
        />
      </Drawer.Navigator>
    </NavigationContainer>
  );
}
