import React from "react";
import {
  StyleSheet,
  StatusBar,
  View,
  Text,
  SafeAreaView,
  Image,
  ImageBackground,
  ScrollView,
  Picker,
} from "react-native";

import { Avatar, ProgressBar } from "react-native-paper";
import result from "./test.json";
import coalition from "./coallition.json";
const Flex = ({ route, rslt, dark }) => {
  // let { result, coalition } = rslt;
  function coalition_style(bgcolor = "none") {
    return {
      backgroundColor: bgcolor,
      maxWidth: 60,
      maxHeight: 100,
      minWidth: 60,
      minHeight: 100,
      borderWidth: 2,
      borderColor: "transparent",
      borderBottomLeftRadius: 100,
      borderBottomRightRadius: 100,
      flex: 0.15,
    };
  }
  function inStyle(color = "#A061D1") {
    return {
      color: color,
      textAlign: "left",
      fontWeight: "500",
      fontSize: 14,
    };
  }
  function titleText(color = "#ffffff", bold = "bold", size = 19) {
    return {
      fontSize: size,

      fontWeight: bold,
      color: color,
    };
  }
  // coalition = coalition.length > 0 ? coalition[0] : coalition;
  // const image = {
  //   uri: `${result?.image_url}`,
  // };
  const cover = {
    uri: `${coalition?.cover_url}`,
  };
  const img_coallition = {
    uri: `${coalition?.image_url}`,
  };
  return (
    <SafeAreaView>
      <ScrollView>
        <View
          style={[
            styles.container,
            {
              flexDirection: "column",
              flex: 1,
              // position: "relative",
            },
          ]}
        >
          {result ? (
            <>
              <StatusBar
                animated={true}
                backgroundColor={coalition?.color}
                hidden={false}
              />

              <ImageBackground
                source={cover}
                resizeMode="cover"
                style={styles.image}
              >
                <View
                  style={{
                    position: "absolute",
                    top: 0,
                    left: 20,
                  }}
                >
                  <View style={coalition_style(coalition?.color)}>
                    <Image
                      source={img_coallition}
                      style={styles.img_coallition}
                    />
                  </View>
                  <Text style={titleText(coalition?.color, "500", 14)}>
                    {coalition?.name}
                  </Text>
                </View>
                <View
                  style={{
                    flexDirection: "row",
                    flexWrap: "wrap",
                    alignContent: "center",
                    justifyContent: "center",
                  }}
                >
                  <Avatar.Image
                    size={150}
                    source={{ uri: `${result.image_url}` }}
                    style={styles.img}
                  />
                </View>
                <Picker
                  style={{
                    backgroundColor: "#000",
                    color: "#ffff",
                    opacity: "80%",
                    width: "5%",
                  }}
                >
                  <Picker.Item label="Java" value="java" />
                  <Picker.Item label="JavaScript" value="js" />
                </Picker>
              </ImageBackground>
              <Text
                style={[
                  titleText(coalition?.color),
                  { textAlign: "center", lineHeight: 50 },
                ]}
              >
                {result?.login}
              </Text>
              <Text
                style={[
                  titleText(!dark ? "#000" : "#ffffff"),
                  { textAlign: "center", lineHeight: 50 },
                ]}
              >
                {result?.first_name} {result?.last_name}
              </Text>
              {result.cursus_users[0]?.grade ? (
                <View
                  style={{
                    padding: 20,
                    flexDirection: "row",
                    flexWrap: "wrap",
                    justifyContent: "space-between",
                    alignItems: "stretch",
                  }}
                >
                  <Text style={inStyle(coalition?.color)}>Grade</Text>
                  <Text
                    style={[
                      titleText(!dark ? "#000" : "#ffffff", "500", 14),
                      { textAlign: "right" },
                    ]}
                  >
                    {result.cursus_users[0]?.grade}
                  </Text>
                </View>
              ) : (
                <></>
              )}
              <View
                style={{
                  padding: 20,
                  flexDirection: "row",
                  flexWrap: "wrap",
                  justifyContent: "space-between",
                  alignItems: "stretch",
                }}
              >
                <Text style={inStyle(coalition.color)}>Email</Text>
                <Text
                  style={[
                    titleText(!dark ? "#000" : "#ffffff", "500", 14),
                    { textAlign: "right" },
                  ]}
                >
                  {result.email}
                </Text>
              </View>
              {result?.phone != "hidden" ? (
                <View
                  style={{
                    padding: 20,
                    flexDirection: "row",
                    flexWrap: "wrap",
                    justifyContent: "space-between",
                    alignItems: "stretch",
                  }}
                >
                  <Text style={inStyle(coalition?.color)}>Phone</Text>
                  <Text
                    style={[
                      titleText(!dark ? "#000" : "#ffffff", "500", 14),
                      { textAlign: "right" },
                    ]}
                  >
                    {result?.phone}
                  </Text>
                </View>
              ) : (
                <></>
              )}
              <View
                style={{
                  padding: 20,
                  flexDirection: "row",
                  flexWrap: "wrap",
                  justifyContent: "space-between",
                  alignItems: "stretch",
                }}
              >
                <Text style={inStyle(coalition.color)}>Wallet</Text>
                <Text
                  style={[
                    titleText(!dark ? "#000" : "#ffffff", "500", 14),
                    { textAlign: "right" },
                  ]}
                >
                  {result?.wallet}
                </Text>
              </View>
              <View
                style={{
                  padding: 20,
                  flexDirection: "row",
                  flexWrap: "wrap",
                  justifyContent: "space-between",
                  alignItems: "stretch",
                }}
              >
                <Text style={inStyle(coalition.color)}>Correction point</Text>
                <Text
                  style={[
                    titleText(!dark ? "#000" : "#ffffff", "500", 14),
                    { textAlign: "right" },
                  ]}
                >
                  {result.correction_point}
                </Text>
              </View>
              <View
                style={{
                  padding: 20,
                  flexDirection: "row",
                  flexWrap: "wrap",
                  justifyContent: "space-between",
                  alignItems: "stretch",
                }}
              >
                <Text style={inStyle(coalition.color)}>Level</Text>
                <Text
                  style={[
                    titleText(!dark ? "#000" : "#ffffff", "500", 14),
                    { textAlign: "right" },
                  ]}
                >
                  {result.cursus_users[0].level}
                </Text>
              </View>

              <View
                style={{ width: "100%", padding: 20, alignItems: "center" }}
              >
                {result.cursus_users[0].skills.map((el, key) => {
                  return (
                    <>
                      <Text
                        key={key}
                        style={titleText(
                          !dark ? "#000" : "#ffffff",
                          "normal",
                          15
                        )}
                      >
                        {el.name}
                      </Text>
                      <ProgressBar
                        key={key}
                        progress={`0${el.level.toString().match(/.\d+/g)}`}
                        color={coalition.color}
                        style={{ width: 250 }}
                      />
                    </>
                  );
                })}
              </View>
            </>
          ) : (
            ""
          )}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // padding: 20,
  },
  image: {
    // flex: 1,
    justifyContent: "center",
    width: "100%",
    height: 250,
  },
  img: {
    width: 150,
    height: 150,
    borderRadius: 100,
    // marginTop: 10,
    // marginBottom: 10
  },

  img_coallition: {
    width: 50,
    height: 50,
  },
});

export default Flex;
