import React from "react";
import {
  StyleSheet,
  StatusBar,
  View,
  Text,
  SafeAreaView,
  Image,
  ImageBackground,
} from "react-native";
import { ProgressBar } from "react-native-paper";
import { Avatar } from "react-native-paper";

const Flex = ({ route, result }) => {
  // console.log(route.params);
  const { coalition } = result;
  const rslt = result?.result;
  console.log(result.result);
  function coalition_style(bgcolor = "#68BBE3") {
    return {
      backgroundColor: bgcolor,
      maxWidth: 60,
      maxHeight: 100,
      minWidth: 60,
      minHeight: 100,
      borderWidth: 2,
      borderColor: "transparent",
      borderBottomLeftRadius: 100,
      borderBottomRightRadius: 100,
      flex: 0.15,
    };
  }
  function inStyle(color = "#A061D1") {
    return {
      color: color,
      textAlign: "left",

      fontSize: 14,
    };
  }
  function titleText(color = "#000") {
    return {
      fontSize: 19,
      lineHeight: 50,
      fontWeight: "bold",
      color: color,
      textAlign: "center",
    };
  }
  console.log(coalition[0].color);
  const image = {
    uri: `${rslt?.image_url}`,
  };
  const cover = {
    uri: `${coalition[0]?.cover_url}`,
  };
  const img_coallition = {
    uri: `${coalition[0]?.image_url}`,
  };
  return (
    // <SafeAreaView>
    <View
      style={[
        styles.container,
        {
          flexDirection: "column",
          flex: 1,
        },
      ]}
    >
      <StatusBar
        animated={true}
        backgroundColor={coalition[0].color}
        hidden={false}
      />
      <ImageBackground source={cover} resizeMode="cover" style={styles.image}>
        <View style={{ flexDirection: "row", flexWrap: "wrap", top: 0 }}>
          <View style={coalition_style(coalition[0].color)}>
            <Image source={img_coallition} style={styles.img_coallition} />
          </View>
          <Avatar.Image
            size={200}
            source={{ uri: `${rslt.image_url}` }}
            style={styles.img}
          />
        </View>
        <View style={{ backgroundColor: "black", opacity: "70%", padding: 20 }}>
          <Text style={titleText(coalition[0].color)}>{rslt.login}</Text>
          <Text style={styles.nameStyle}>
            {rslt.first_name} {rslt.last_name}
          </Text>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              justifyContent: "space-between",
            }}
          >
            <Text style={inStyle(coalition[0].color)}>Grade</Text>
            <Text style={styles.textStyle}>{rslt.cursus_users[0].grade}</Text>
          </View>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              justifyContent: "space-between",
            }}
          >
            <Text style={inStyle(coalition[0].color)}>Email</Text>
            <Text style={styles.textStyle}>{rslt.email}</Text>
          </View>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              justifyContent: "space-between",
            }}
          >
            <Text style={inStyle(coalition[0].color)}>Phone</Text>
            <Text style={styles.textStyle}>{rslt.phone}</Text>
          </View>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              justifyContent: "space-between",
            }}
          >
            <Text style={inStyle(coalition[0].color)}>Wallet</Text>
            <Text style={styles.textStyle}>{rslt.wallet}</Text>
          </View>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              justifyContent: "space-between",
            }}
          >
            <Text style={inStyle(coalition[0].color)}>Correction point</Text>
            <Text style={styles.textStyle}>{rslt.correction_point}</Text>
          </View>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              justifyContent: "space-between",
            }}
          >
            <Text style={inStyle(coalition[0].color)}>Level</Text>
            <Text style={styles.textStyle}>{rslt.cursus_users[0].level}</Text>
          </View>
        </View>
      </ImageBackground>

      <ProgressBar
        progress={"0" + rslt.cursus_users[0].level.toString().match(/.\d+/g)}
        color={`${coalition[0].color}`}
        style={{ height: 20, backgroundColor: "white" }}
      ></ProgressBar>
      {/* </SafeAreaView> */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // padding: 20,
  },
  image: {
    flex: 2,
    justifyContent: "center",
  },
  nameStyle: {
    color: "white",
    textAlign: "center",

    fontSize: 17,
  },
  textStyle: {
    color: "white",
    textAlign: "right",

    fontSize: 14,
  },
  img: {
    width: 200,
    height: 200,
    borderRadius: 100,
    margin: "auto",
  },
  img_coallition: {
    width: 50,
    height: 50,
    margin: "auto",
  },
});

export default Flex;
